pragma solidity ^0.5.0;

contract Purchase{
	address [16] public buyers;
	string public name;
	mapping(uint => Product) public products;

	struct Product {
		uint productId;
		string name;
		uint price;
		address payable owner;
		bool purchased;

	}
	event ProductCreated(
        uint productId,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );

    event ProductPurchased(
        uint productId,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );
    constructor() public {
    	name = "My cute lil marketplace";
    }
    // function createProduct(string memory _name, uint _price, uint _productId) public {
    // 	require(bytes(_name).length > 0);
    // 	require(_price >0);
    // 	products[_productId] = Product(_productId, _name, _price, msg.sender, false);
    // 	emit ProductCreated(_productId, _name, _price,msg.sender,false);
    // } 

	function buy(uint _productId) public payable returns(uint){
		Product memory _product = products[_productId];
		address payable _seller = _product.owner;
		require(msg.value>= _product.price);
		require(!_product.purchased);
		//check to see if oroduct id is in range of purchasers
		require(_productId >= 0 && _productId <= 15);
		_product.purchased = true;
		//if in range, add purchaser to array by smart contract that called
		buyers[_productId] = msg.sender;

		address(_seller).transfer(msg.value);
        // Trigger an event
        emit ProductPurchased(_productId, _product.name, _product.price, msg.sender, true);
		//confirm
		products[_productId] = _product;
		return _productId;
	}

	function getPurchasers() public view returns (address[16] memory) {
		return buyers;
	}
}
